const audio = this.getDBM().Audio;
if(audio.playingnow && audio.playingnow[server.id]){
var obj = audio.playingnow[server.id][2];
this.storeValue(obj, 1, "nowplaying-url", cache);
}

const Audio = this.getDBM().Audio;
const id = server.id;
if(Audio && Audio.queue[id]) {
if(Audio.queue[id].length > 0) {
   const left = Audio.queue[id].length;
   this.storeValue(left + " songs before!", 1 , "queue-script", cache);
} else {
   this.storeValue("Next song.", 1 , "queue-script", cache);
}
} else {
this.storeValue(" ", 1, "queue-script", cache);
}
